import axios from "axios";




const addPj = (pj)=>{
    axios.post('http://localhost:3002/rickandmorty/fav',{...pj})
    return{
        type: "ADD_PJ",
        payload: pj
    }
}

const deletePj = (id) =>{
    axios.delete(`http://localhost:3002/rickandmorty/fav/${id}`)
    return{
        type: "DELETE_PJ",
        payload: id
    }
}

const filterCards = (status,order) =>{
    return{
        type:"FILTER",
        payload: {status,order}
    }
}


const oderCards = (id) =>{
    return{
        type:"ORDER",
        payload: id
    }
}




module.exports= {
    addPj,
    deletePj,
    filterCards,
    oderCards
}
