import axios from "axios";

export const addPj = async (pj) => {
  const { name, id, species, gender, image } = pj;
  const addFavorites = async () => {
    try {
      await axios
        .post("http://localhost:3002/rickandmorty/fav", {
          id,
          name,
          species,
          gender,
          image,
        })
        .then((res) => {
          return {
            type: "ADD_PJ",
            payload: pj,
          };
        });
    } catch (err) {
      window.alert("Character can't be deteted");
    }
  };

  //addFavorites();
  /*
  return {
    type: "ADD_PJ",
    payload: pj,
  };
  */
};

export const deletePj = (id) => {
  const removeFavorites = async () => {
    try {
      await axios.delete(`http://localhost:3002/rickandmorty/fav/${id}`);
    } catch (err) {
      window.alert("Character can't be deteted");
    }
  };
  removeFavorites();

  return {
    type: "DELETE_PJ",
    payload: id,
  };
};

export const filterCards = (status, order) => {
  return {
    type: "FILTER",
    payload: { status, order },
  };
};

export const oderCards = (id) => {
  return {
    type: "ORDER",
    payload: id,
  };
};
