return async function(dispatch){
  try {
   await axios.post("http://localhost:3002/rickandmorty/fav", {name , id, species, gender, image });
    return dispatch(
      {
        type: "ADD_PJ",
        payload: pj,
      });
    
  } catch (err) {
    window.alert("Character can't be deteted" + err.message);
  }
}
};

export const deletePj =  (id) => {

    const removeFavorites = async() =>{
        try {
            await axios.delete(`http://localhost:3002/rickandmorty/fav/${id}`);
        } catch (err) {
            window.alert("Character can't be deteted");
        }
    }
    removeFavorites();

    return {
      type: "DELETE_PJ",
      payload: id,
    };
 
    
  
};

export const filterCards = (status, order) => {
  return {
    type: "FILTER",
    payload: { status, order },
  };
};

export const oderCards = (id) => {
  return {
    type: "ORDER",
    payload: id,
  };
};
