import axios from "axios";

export const addPj =  (pj) => {
  
const{name, id, species, gender, image}= pj;  

return async function(dispatch){
  try {
   await axios.post("http://localhost:3002/rickandmorty/fav", {name , id, species, gender, image });
    dispatch(
      {
        type: "ADD_PJ",
        payload: pj,
      });
    
  } catch (err) {
    window.alert("Character not found " + err.message);
  }
}
};

export const deletePj =  (id) => {

    
return async function(dispatch){
  try {
    await axios.delete(`http://localhost:3002/rickandmorty/fav/${id}`);
    dispatch(
      {
        type: "DELETE_PJ",
        payload: id,
      });
    
  } catch (err) {
    window.alert("Character can't be deleted " + err.message);
  }
}
};
    
  


export const filterCards = (status, order) => {
  return {
    type: "FILTER",
    payload: { status, order },
  };
};

export const oderCards = (id) => {
  return {
    type: "ORDER",
    payload: id,
  };
};
