const initialState = {
    myFavorites:[],
    allCharacters:[]
}

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type) {
  case "ADD_PJ":
    return { ...state, allCharacters: [...state.allCharacters, payload] ,myFavorites:[...state.myFavorites, payload]}
    case "DELETE_PJ":
        return {...state, myFavorites: [...state.myFavorites].filter((x)=>x.CharacterId !== payload)}
    case "FILTER":
      return {...state, allCharacters: payload.order=== "Ascendente"?[...state.myFavorites].filter((x)=>x.gender===payload.status).sort((a,b)=>a.CharacterId-b.CharacterId):[...state.myFavorites].filter((x)=>x.gender===payload.status).sort((a,b)=>b.CharacterId-a.CharacterId) }
    case "ORDER":
      return {...state, allCharacters: payload==="Ascendente"?[...state.myFavorites].sort((a,b)=>a.CharacterId-b.CharacterId): [...state.myFavorites].sort((a,b)=>b.CharacterId-a.CharacterId) }

    case "GET_PJ":
      return {...state, myFavorites: payload}
  default:
    return state
  }
}


export default rootReducer;
