import axios from "axios";

export const addPj = async (pj) => {
  try {
    const result= await axios.post("http://localhost:3002/rickandmorty/fav", { ...pj });
    if(result){
        return {
            type: "ADD_PJ",
            payload: pj,
          };
    }
    
  } catch (err) {
    window.alert("Character can't be deteted");
  }
};

export const deletePj = async (id) => {
  try {
    await axios.delete(`http://localhost:3002/rickandmorty/fav/${id}`);
    return {
      type: "DELETE_PJ",
      payload: id,
    };
  } catch (err) {
    window.alert("Character can't be deteted");
  }
};

export const filterCards = (status, order) => {
  return {
    type: "FILTER",
    payload: { status, order },
  };
};

export const oderCards = (id) => {
  return {
    type: "ORDER",
    payload: id,
  };
};
